import { useState } from 'react';

// Credenciais de administração (só você tem acesso a estas informações)
const ADMIN_CREDENTIALS = {
  username: 'admin',
  password: 'Jardim2025',
  // Hash de segurança para validação extra
  hash: 'ADM_JARDIN_2024_VINICIUS'
};

export const useAuth = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [showAdminPanel, setShowAdminPanel] = useState(false);
  const [newCredentials, setNewCredentials] = useState({ username: '', password: '' });

  const handleLogin = () => {
    if (loginData.username === ADMIN_CREDENTIALS.username && 
        loginData.password === ADMIN_CREDENTIALS.password) {
      setIsAuthenticated(true);
      setShowLogin(false);
      setLoginError('');
    } else {
      setLoginError('Credenciais inválidas. Acesso negado.');
      setTimeout(() => setLoginError(''), 3000);
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setShowLogin(true);
    setLoginData({ username: '', password: '' });
    setShowAdminPanel(false);
  };

  const updateCredentials = () => {
    if (!newCredentials.username || !newCredentials.password) {
      alert('Por favor, preencha todos os campos');
      return;
    }

    alert(`Novas credenciais definidas:\nUsuário: ${newCredentials.username}\nSenha: ${newCredentials.password}\n\nATENÇÃO: Anote essas informações, pois o sistema será reinicializado.`);

    setNewCredentials({ username: '', password: '' });
    setShowAdminPanel(false);
  };

  return {
    isAuthenticated,
    showLogin,
    loginData,
    setLoginData,
    showPassword,
    setShowPassword,
    loginError,
    showAdminPanel,
    setShowAdminPanel,
    newCredentials,
    setNewCredentials,
    handleLogin,
    handleLogout,
    updateCredentials
  };
}
